import groovy.json.JsonSlurper
node(){
    print pr
    env.pr=pr
    print commitid
    env.repo=repo
    print repo
    print branch
    //http://35.223.53.23:7990/rest/api/latest/projects/DEM/repos/jenkinspr/pull-requests/1/changes
    def json = sh label: '', returnStdout: true, 
                script: '''json=$(curl -k -u admin:admin http://35.223.53.23:7990/rest/api/latest/projects/JEN/repos/$repo/pull-requests/$pr/changes?limit=1000)
                echo "$json" > test.json'''
    json=readJSON file: 'test.json'
    //def slurper = new JsonSlurper()
    //def result = slurper.parseText(json)
    def list = json.get("values")
    def list_files= []
    //print list
    for (int i=0;i<list.size();i++){
        def val = list[i].get("path")
        if(!list_files.contains(val.get("toString")))
            list_files.add(val.get("toString"))
    }

    print "==============================================="
    print list_files
	env.list_files=list_files
    stage('Checkout'){
        checkout changelog: false, poll: false, scm: [$class: 'GitSCM', branches: [[name: "$branch"]], doGenerateSubmoduleConfigurations: false, extensions: [], submoduleCfg: [], userRemoteConfigs: [[credentialsId: '36686a20-adb7-47ac-999f-4e5c2d9a3dbe', url: "http://35.223.53.23:7990/scm/JEN/"+repo+".git"]]]
    }
	stage('Copy Files'){
		for(int j=0;j<list_files.size();j++){
			env.file=list_files[j]
			sh'''
				sudo cp -rfv --parent $file /mnt/jenkinsbackup/
			'''
		}
	}
    deleteDir()
}
